// main js const swiper=new Swiper('.projects .swiper',{slidesPerView:4,slidesPerGroup:1,spaceBetween:30,loop:true,// Optional parameters direction:'horizontal',// If we need pagination pagination:{el:'.projects .swiper-pagination',clickable:true},// Navigation arrows navigation:{nextEl:'.projects .swiper-button-next',prevEl:'.projects .swiper-button-prev',},// And if we need scrollbar scrollbar:{el:'.projects .swiper-scrollbar',},breakpoints:{// моб 320:{slidesPerView:1,spaceBetween:10},// планш 768:{slidesPerView:2,spaceBetween:20},// пк 1024:{slidesPerView:3,spaceBetween:30}}});document.addEventListener('DOMContentLoaded',function(){const hamburger=document.querySelector('.hamburger');const menu=document.querySelector('.menu');hamburger.addEventListener('click',function(){hamburger.classList.toggle('active');menu.classList.toggle('active')});// Закрываем меню при клике на ссылку (для мобильных) const menuLinks=document.querySelectorAll('.menu a');menuLinks.forEach(link=>{link.addEventListener('click',function(){if (window.innerWidth < 768){hamburger.classList.remove('active');menu.classList.remove('active')}})});const theme=document.getElementById('theme');// проверка сохр-й темы const savedTheme=localStorage.getItem('theme') || 'dark';document.documentElement.setAttribute('data-theme',savedTheme);// обр-к клика theme.addEventListener('click',function(){const currentTheme=document.documentElement.getAttribute('data-theme');const newTheme=currentTheme==='dark' ? 'light':'dark';document.documentElement.setAttribute('data-theme',newTheme);localStorage.setItem('theme',newTheme)});//анимация при прокрутке const elements=document.querySelectorAll('.profile__photo, .profile__text, .skills__item, .projects__back, .services__back, .contacts__block');function checkVisibility(){elements.forEach(element=>{const position=element.getBoundingClientRect().top;const screenHeight=window.innerHeight;if (position < screenHeight - 50){element.classList.add('visible')}})}//пров-ка при загрузке и прокрутке checkVisibility();window.addEventListener('scroll',checkVisibility)});